export default function ExampleDemo ({children}) {

    return (
        <div className="ExampleDemo">
            <div> Example: {children} </div>
            <div> code: <pre><code>
      {JsxString(children)}
      </code></pre></div>
           <hr/>

        </div>

    )
}

const JsxString = (component, counter = 0) => {
    let type = component.type.name;
    let props = component.props;
    let propsString = "";
    for (let key in props) {
      if (key !== "children") {
        let propValue = props[key];
        let value = "";
        if (propValue instanceof Object) {
          value = `{${JSON.stringify(propValue).replace(/['"]+/g, '')}}`;
        } else {
          value = `"${propValue}"`;
        }
        propsString += ` ${key}=${value}`;
      }
    }
    if (props.children) {
      counter += 2;
      var children = JsxString(props.children, counter);
      return `<${type}${propsString}>
  ${Array(counter).join(" ")}  ${children}
  ${Array(counter).join(" ")}</${type}>`;
    }
    return `<${type}${propsString} />`;
  }
