import {useState} from 'react';
import { Container, Align, Heading } from "../../components/";

export default function System() {

    const [controlsOpen, setControlsOpen] = useState(false);

    function handleClick() {
        setControlsOpen(!controlsOpen)
    }
    return (
        <div className="design-system-layout">
            <style jsx>{`
                .design-system-layout {
                    display: grid;
                    grid-template-columns: 340px 1fr;
                }

                .main {
                    background: var(--grey-800)
                }

                aside {

                    z-index: 100;
                    height: 100vh;
                }

                .sidebar__inner {
                    position: absolute;
                    top: 0;
                    left: 0;
                    padding: 1rem;
                    background: white;
                    height: 100vh;
                    width: 340px;
                    border-right: 1px solid var(--grey-400);
                }

                .sidebar--offscreen {
                    transition: transform ease-in-out 0.5s;
                    transform: ${controlsOpen ? 'translateX(-340px)' : 'translateX(0)' } ;
                }
            `}</style>
            <aside>
                <div className="sidebar__inner">
                    <ul>
                        <li>Image</li>
                        <li>Some other item</li>
                        <li>Image</li>
                        <li>Some other item</li>
                        <li>Image</li>
                        <li>Some other item</li>
                    </ul>
                </div>
                <div className="sidebar__inner sidebar--offscreen">sidebar</div>
            </aside>
            <main> 

            <div className="main">

            <Align horizontal="center">
                <Heading size="4rem" theme="light" className='test'>Matthew Evans</Heading>
            </Align>

            <Align horizontal="center"> 
            UX Designer with a strong background in email &amp; UI. <br/>
            Codes and animates a bit here and there.</Align>

            <button onClick={()=>handleClick()}>Click</button>

        </div>
            </main>

        </div>

    )
}
