import styles from "./Container.module.css"

export default function Container({background, children}) {

    return (
        <div className="containerBackground">
            <style jsx>{`
        .containerBackground {
          background: ${props.background == 'background' ? '#fff3cd' : '#eee'};
        }
      `}</style>
            {background}
            <div className={styles.container}>{children}</div>
        </div>

    )
}
