import styled from 'styled-components';

const Container = styled.main`
    ${props => props.background === 'dark' ? `background: var(--grey-100)` : ''};
    max-width: 70rem;;
    margin: 0 auto;
    font-size: 1em;
    border: 0;
    padding: 0.6rem 2rem;
`;

export default Container
