import { Container, Align, Heading } from "../../components/";

export default function System() {
    return (
        <div className="design-system-layout">
            <style jsx>{`
                .design-system-layout {
                    display: grid;
                    grid-template-columns: 340px 1fr;
                }

                .main {
                    background: var(--accent-dark-100);
                }

                aside {
                    box-shadow: 1px 0 0 0 black;
                    padding: 1rem;
                }
            `}</style>
            <aside>
                <div className="sidebar__inner">navigation</div>
                <div className="sidebar__inner">sidbar</div>
            </aside>
            <main> 

            <div className="main">

            <Align horizontal="center">
                <Heading size="4rem" theme="dark" className='test'>Matthew Evans</Heading>
            </Align>

            <Align horizontal="center"> 
            UX Designer with a strong background in email &amp; UI. <br/>
            Codes and animates a bit here and there.</Align>



        </div>
            </main>

        </div>

    )
}
