import styled from 'styled-components';

const Button = styled.button`
  /* Adapt the colors based on primary prop */
   ${props => props.type ? `background: var(-grey-900)` : `background: var(--accent-light-400)`};
   ${props => props.type ? `color: var(--accent-light-400)` : `color: var(-accent-light-900)`};
    font-size: 1em;
    margin: 1em;
    border: 0;
    padding: 0.25em 1em;
    border-radius: 3px;
    cursor: pointer;

    &:hover {
        border: 2px solid red;
    }
`;

export default Button
