import styled from 'styled-components';

const Container = styled.main`
    ${props => props.background === 'dark' ? `background: var(--grey-100)` : ''};
    font-size: 1em;
    border: 0;
    padding: 0.6rem 2rem;
`;

const ContainerContents = styled.div`
    max-width: 70rem;
    margin: 0 auto;
`

export default Container
