import styled from 'styled-components';

const ContainerWrapper = styled.main`
    ${background === 'dark' ? `background: var(--grey-100)` : ''};
    font-size: 1em;
    border: 0;
    padding: 0.6rem 2rem;
`;

const ContainerContents = styled.div`
    max-width: 70rem;
    margin: 0 auto;
`;

const Container = ({children, background}) => {
    return (
      <ContainerWrapper>
        <ContainerContents>
            {children}
        </ContainerContents>
      </ContainerWrapper>
    );
  };

export default Container
