export default function Align({children, title, value}) {

    return (
        <div className="control-input">
            <style jsx>{`
                .control-input {
                    display: flex;
                    flex-direction: column;
                }
            `}</style>
            <label htmlFor={title}>{title}</label>
            <input type="text" id={title} defaultValue={value}/>
            {children}
            </div>
    )
}
