import {useState} from 'react';
import { Container, Align, Heading, ControlInput } from "../../components/";

export default function System() {

    const [controlsOpen, setControlsOpen] = useState(false);

    function handleClick() {
        setControlsOpen(!controlsOpen)
    }
    return (
        <div className="design-system-layout">
            <style jsx>{`
                .design-system-layout {
                    display: grid;
                    grid-template-columns: 340px 1fr;
                }

                .main {
                    background: var(--grey-800);
                    padding: 2rem;
                }

                aside {
                    z-index: 100;
                    height: 100vh;
                }

                .sidebar__inner {
                    position: absolute;
                    top: 0;
                    left: 0;
                    padding: 1rem;
                    background: var(--grey-800);
                    height: 100vh;
                    width: 340px;
                    border-right: 1px solid var(--grey-400);
                }

                .sidebar--offscreen {
                    transition: transform ease 0.5s;
                    transform: ${!controlsOpen ? 'translateX(-340px)' : 'translateX(0)' } ;
                }

                .controls-form {
                    display: flex;
                    flex-direction: column;
                }
            `}</style>
            <aside>
                <div className="sidebar__inner">
                    <ul>
                        <li>Image</li>
                        <li>Some other item</li>
                        <li>Image</li>
                        <li>Some other item</li>
                        <li>Image</li>
                        <li>Some other item</li>
                    </ul>
                </div>
                <div className="sidebar__inner sidebar--offscreen">
                     <Heading size="1rem" theme="light">Image card</Heading>
                    <div className="controls-form">
                    <ControlInput title="title" value="Brandwatch" inputType="string" />
                    <ControlInput title="width" value="10rem" inputType="number" />
                    <ControlInput title="height" value="10rem" inputType="number" />
                    <ControlInput title="desc" value="Project about XYZ" inputType="string" />
                    </div>

                </div>
            </aside>
            <main> 

            <div className="main">


            <Heading size="2rem" theme="light">Image card </Heading>
            <p style={{maxWidth: '500px'}}>An image card that displays a preview of a portfolio item. Animation effects are reccomended when using cards.</p>
            <button onClick={()=>handleClick()}>Click</button>

        </div>
            </main>

        </div>

    )
}
