export default function Align({children, title, value, inputType}) {

    return (
        <div className="control-input">
            <style jsx>{`
                .control-input {
                    display: flex;
                    flex-direction: column;
                } 
                .control-input__wrapper {
                    width: 100%;
                    position: relative;
                }
                .control-input__input {
                    width: 100%;
                    font-size: 13px;
                    line-height: 1.4rem;
                    border: 0;
                    border-bottom: 1px solid var(--grey-100);
                } 
                .control-input__input:focus {
                    outline: 0;
                }
                .control-input__inputType {
                    position: absolute;
                    top: 0;
                    right: 0;
                    font-size: 13px;
                    line-height: 1.4rem;
                    color: var(--grey-200);
                }

            `}</style>
            <label htmlFor={title}>{title}</label>
            <div className="control-input__wrapper">
                <input className="control-input__input" type="text" id={title} defaultValue={value}/>
                <span className="control-input__inputType">{inputType}</span>
            </div>
            {children}
            </div>
    )
}
