import {useState} from 'react';
import { Container, Align, Heading, ControlInput, ImageCard } from "../../components/";
import ImageCardControls from "../../components/ImageCard/ImageCardControls"

export default function System() {

    const [controlsOpen, setControlsOpen] = useState(false);
    const [currentValues, setCurrentValues] = useState(ImageCardControls);


    function handleClick() {
        setControlsOpen(!controlsOpen)
    }
    return (
        <div className="design-system-layout">

            <aside>
                <div className="sidebar__inner">
                    <ul>
                        <li>Image</li>
                        <li>Some other item</li>
                        <li>Image</li>
                        <li>Some other item</li>
                        <li>Image</li>
                        <li>Some other item</li>
                    </ul>
                </div>
                <div className="sidebar__inner sidebar--offscreen">
                    <Heading size="1rem" theme="light">Image card</Heading>
                    <div className="controls-form">
                    {
                        ImageCardControls.map((item, index) => (
                            <>
                            {console.log(index)}
                             <ControlInput 
                                title={item.name} 
                                value={item.value} 
                                inputType={item.inputType} 
                                onChange={() => setCurrentValues(currentValues)}/>
                            </>
                        ))
                    } 
                    {/* <ControlInput title="title" value="Brandwatch" inputType="string" />
                    <ControlInput title="width" value="10rem" inputType="number" />
                    <ControlInput title="height" value="10rem" inputType="number" />
                    <ControlInput title="desc" value="Project about XYZ" inputType="string" />
                    <ControlInput title="image" value="https://source.unsplash.com/collection/1212322/280x280" inputType="image path" /> */}
                    <div>
                    <pre>
                    &lt;Card  <br />
                    {
                        ImageCardControls.map(item => (
                            <>
                             {item.name}={item.value}<br/>
                            </>
                        ))
                    }
                    />  <br />
                    </pre>

                    </div>
                    currentValues:
                    {currentValues.map(currentValue => (
                        currentValue.name
                    ))}
                </div>

                </div>
            </aside>
            <main> 

            <div className="main">


            <Heading size="2rem" theme="light">Image card </Heading> 
            <p style={{maxWidth: '480px'}}>An image card that displays a preview of a portfolio item. Animation effects are reccomended when using cards.</p>
            <button onClick={()=>handleClick()} className="button">Controls</button> <br/>
            Preview

            <hr/>
            <img src="https://source.unsplash.com/collection/1212322/280x280" alt=""/>
            <ImageCard>test</ImageCard> 

        </div>
            </main>

        </div>

    )
}
