import {useState} from 'react';
import { Container, Align, Heading, ControlInput } from "../../components/";

export default function System() {

    const [controlsOpen, setControlsOpen] = useState(false);

    function handleClick() {
        setControlsOpen(!controlsOpen)
    }
    return (
        <div className="design-system-layout">
            <style jsx>{`
                .design-system-layout {
                    display: grid;
                    grid-template-columns: 340px 1fr;
                    background: var(--grey-800);
                }

                .main {
                    padding: 2rem;
                    max-width: 900px;
                    margin: 0 auto;
                }

                aside {
                    z-index: 100;
                    height: 100vh;
                }

                .sidebar__inner {
                    position: absolute;
                    top: 0;
                    left: 0;
                    padding: 1rem;
                    background: var(--grey-800);
                    height: 100vh;
                    width: 340px;
                    border-right: 1px solid var(--grey-400);
                }

                .sidebar--offscreen {
                    transition: transform ease 0.5s;
                    transform: ${!controlsOpen ? 'translateX(-340px)' : 'translateX(0)' } ;
                }

                .controls-form {
                    display: flex;
                    flex-direction: column;
                }

                .button {
                    padding: 0.5rem 0.8rem;
                    outline: none;
                    border: none;
                    background: var(--accent-dark-500);
                    color: var(--grey-900);
                    font-family: wotfardbold;
                    text-transform: uppercase;
                    cursor: pointer;
                    border-radius: 0.4rem;
                    margin-bottom: 1rem;
                }

                p {
                    font-size: 16px;
                    color: #1B393D;
                    line-height: 28px;
                }
            `}</style>
            <aside>
                <div className="sidebar__inner">
                    <ul>
                        <li>Image</li>
                        <li>Some other item</li>
                        <li>Image</li>
                        <li>Some other item</li>
                        <li>Image</li>
                        <li>Some other item</li>
                    </ul>
                </div>
                <div className="sidebar__inner sidebar--offscreen">
                    <Heading size="1rem" theme="light">Image card</Heading>
                    <div className="controls-form">
                    <ControlInput title="title" value="Brandwatch" inputType="string" />
                    <ControlInput title="width" value="10rem" inputType="number" />
                    <ControlInput title="height" value="10rem" inputType="number" />
                    <ControlInput title="desc" value="Project about XYZ" inputType="string" />
                    <ControlInput title="image" value="https://source.unsplash.com/collection/190727/280x280" inputType="image path" />
                    <div>
                    <pre>
                    &lt;Card  <br />
                    title=&quot;Brandwatch&quot; <br />
                    desc=&quot;Project description &hellip; <br />
                    width=&ldquo;10rem&rdquo; <br />
                    height=&ldquo;10rem&rdquo; <br />
                    image=brandwatchImage/&gt; <br />
                    </pre>
                    </div>

                </div>

                </div>
            </aside>
            <main> 

            <div className="main">


            <Heading size="2rem" theme="light">Image card </Heading> 
            <p style={{maxWidth: '480px'}}>An image card that displays a preview of a portfolio item. Animation effects are reccomended when using cards.</p>
            <button onClick={()=>handleClick()} className="button">Controls</button> <br/>
            <img src="https://source.unsplash.com/collection/190727/280x280" alt=""/>


        </div>
            </main>

        </div>

    )
}
