import styled from 'styled-components';

const Button = styled.button`
   background: var(--accent-light-400);
   ${props => props.type === 'secondary' ? `background: var(--accent-light-400)` : ''};
    font-size: 1em;
    margin: 1em;
    border: 0;
    padding: 0.6rem 2rem;
    border-radius: 3px;
    cursor: pointer;
`;

export default Button
